/**
 * Detect the support of prefetch capability of the browser by using the user-agent data
 *
 * @param agent A parsed by useragent object that contains an information about the browser
 * @returns {boolean}
 */
module.exports = (agent) => {
  const browser = agent.browser.name?.toLowerCase();

  if (['opera mini', 'safari', 'mobile safari'].indexOf(browser) !== -1) {
    return false;
  }

  if (browser === 'ie' && agent.major < 11) {
    return false;
  }
  return true;
};
