/**
 * Detect the support of service workers capability of the browser by using the user-agent data
 *
 * @param agent A parsed by useragent object that contains an information about the browser
 * @returns {boolean}
 */
module.exports = (agent) => {
  const browser = agent.browser.name?.toLowerCase();
  const type = agent.device?.type;

  if (browser === 'chrome' && type === 'mobile' && agent.major >= 45) {
    return true;
  }

  return false;
};
