const React = require('react');

/**
 * I18nContext
 * Saves the I18n instance that is used by the provider
 */
const I18nContext = React.createContext({});

/**
 * useI18n
 * Hook to access the i18n context
 */
const useI18n = () => React.useContext(I18nContext);

/**
 * Expose I18nContext
 */
module.exports = {
  I18nContext,
  useI18n,
};
