const React = require('react');
const I18nJsx = require('./i18n-jsx');

// This class exists only not to include the React scope in the i18n class file.
class i18nJsxCreator {
  static create(text, settings) {
    return <I18nJsx crowdinKey={text} settings={settings} />;
  }
}

module.exports = i18nJsxCreator;
