/**
 * Module dependencies
 */
const { countries } = require('frontend-country_config');

/**
 * Set default language
 */
const defaultLanguage = 'es';

/**
 * All locales available on meli
 */
const locales = [
  defaultLanguage,
  'en',
  'en-US',
];

const reportLocales = [
  'es-AR',
  'pt-BR',
];

/**
 * Only country locales
 */
const countryLocales = {};

/**
 * Populate locales
 */
Object.keys(countries).forEach((id) => {
  const locale = countries[id].locale.replace('_', '-');
  locales.push(locale);
  countryLocales[id] = locale;
});

/**
 * Expose locales
 */
exports.locales = locales;

/**
 * Expose countryLocales
 */
exports.countryLocales = countryLocales;

/**
 * Expose defaultLanguage
 */
exports.defaultLanguage = defaultLanguage;

/**
* Expose defaultLanguage
*/
exports.reportLocales = reportLocales;
