#!/usr/bin/env node

const Svgo = require('../src/svgo');
const { program } = require('commander');

program
  .description('Optimizes svg files and returns a React component')
  .arguments('[from] [to]')
  .action((from, to) => {
    const svgo = new Svgo();
    svgo.createComponents(from, to, (error) => {
      if (error) {
        console.log(error);
        process.exit(1);
      } else {
        console.log('Generate file success');
        process.exit(0);
      }
    });
  });

program.parse(process.argv);
