const React = require('react');
const PropTypes = require('prop-types');
const Script = require('frontend-script');

function CleanSW({ mainDomain }) {
  const cleanswURL = `https://www.${mainDomain.link}/clean-sw.js`;
  return <Script src={cleanswURL} />;
}

CleanSW.propTypes = {
  mainDomain: PropTypes.string.isRequired,
};

module.exports = CleanSW;
