/* eslint-disable nordic/no-style-tags */
const React = require('react');
const Head = require('react-declarative-head');

const Fonts = ({ cdn }) => {
  const getFontUrl = (type, ext) => `${cdn.link}/ui/webfonts/v3.0.0/proxima-nova/proximanova-${type}.${ext}`;
  /* eslint-disable max-len */
  return (
    <Head>
      <link
        rel="preload"
        href={getFontUrl('light', 'woff2')}
        as="font"
        type="font/woff2"
        crossOrigin="anonymous"
      />
      <link
        rel="preload"
        href={getFontUrl('regular', 'woff2')}
        as="font"
        type="font/woff2"
        crossOrigin="anonymous"
      />
      <link
        rel="preload"
        href={getFontUrl('semibold', 'woff2')}
        as="font"
        type="font/woff2"
        crossOrigin="anonymous"
      />
      <style>
        {`@font-face{font-family:'Proxima Nova';font-weight:300;font-display:swap;font-style:normal;src:url(${getFontUrl('light', 'woff2')}) format("woff2"),url(${getFontUrl('light', 'woff')}) format("woff"),url(${getFontUrl('light', 'ttf')}) format("truetype")}@font-face{font-family:'Proxima Nova';font-weight:400;font-display:swap;font-style:normal;src:url(${getFontUrl('regular', 'woff2')}) format("woff2"),url(${getFontUrl('regular', 'woff')}) format("woff"),url(${getFontUrl('regular', 'ttf')}) format("truetype")}@font-face{font-family:'Proxima Nova';font-weight:600;font-display:swap;font-style:normal;src:url(${getFontUrl('semibold', 'woff2')}) format("woff2"),url(${getFontUrl('semibold', 'woff')}) format("woff"),url(${getFontUrl('semibold', 'ttf')}) format("truetype")}`}
      </style>
    </Head>
  );
  /* eslint-enable max-len */
};

module.exports = Fonts;
