/**
 * Create JS snippet to include injected scripts
 * @type {import('../../index.d').getInjectedScripts}
 */
module.exports = function getInjectedScripts({
  inline, lowEnd = false, nonce, src, id,
}) {
  if (lowEnd || (!inline && !src)) {
    return '';
  }

  let scriptTag = '<script';

  if (id) {
    scriptTag = `${scriptTag} id="${id}"`;
  }

  if (nonce) {
    scriptTag = `${scriptTag} nonce="${nonce}"`;
  }

  if (src) {
    scriptTag = `${scriptTag} src="${src}">`; // Close script tag
  } else if (inline) {
    scriptTag = `${scriptTag}>${inline}`; // Close script tag
  }

  return `${scriptTag}</script>`;
};
