/**
 * Extracts and combines the preload header from different bundles
 */
function getPreloadHeader(bundles) {
  const headers = bundles.reduce((h, bundle) => {
    if (bundle && bundle.preload && typeof bundle.preload === 'string') {
      h.push(bundle.preload);
    }

    return h;
  }, []);

  return headers.join(', ');
}

module.exports = getPreloadHeader;
