/**
 * Get style from js for rehydration in client
 * @param {String} css
 * @param {Boolean} enabled
 */
module.exports = function getStyleFromJs(styles, scripts, css, enabled = false) {
  const result = { styles, scripts };

  if (enabled && css && css.content && css.content.length > 0) {
    // Add to styles generated css to rehydration in client
    result.styles = styles.concat(`<style data-aphrodite>${css.content}</style>`);
    // Add to scripts simple rehydration script.
    result.scripts = scripts.concat(`window.renderedClassNames = ${JSON.stringify(css.renderedClassNames)}`);
  }

  return result;
};
