const { CDNS } = require('../../config/head-config');
const getInjectedScripts = require('./get-injected-scripts');

/**
 * Returns HTML5 shiv code
 * @param {object} [cdn=Default CDN] CSS CDN
 * @param {string} cdn.link CSS CDN base URL
 * @param {string} nonceCSP CSP nonce
 * @returns {string} HTML5 shiv row code
 */
const cdnHtml5shiv = (cdn, nonceCSP) => {
  const cdnLink = cdn && cdn.link ? cdn.link : CDNS.default.link;
  return getInjectedScripts({
    src: `${cdnLink}/static/org-img/ch/vendor/html5shiv/html5shiv-3.7.0.js`,
    nonce: nonceCSP,
  });
};

module.exports = {
  cdnHtml5shiv,
};
