/*
    Known picture variants
    -O Original picture with the original size and aspect ratio
    -M 50px square picture
    -S 90px square picture
    -G 120px square picture
 */

const getUserPicture = ({ user, variant }) => {
  const userHasPicture = user && user.raw && user.raw.thumbnail && user.raw.thumbnail.picture_url;
  if (!userHasPicture) return null;
  const picture = user.raw.thumbnail.picture_url;

  return variant
    ? user.raw.thumbnail.picture_url.replace(/-O.jpg$/, `${variant}.jpg`)
    : picture;
};

module.exports = getUserPicture;
