const tagsCredits = [
  'credits_priority_1',
  'credits_priority_2',
  'credits_priority_3',
  'credits_priority_4',
  'credits_open_market',
  'credits_active_borrower',
];

const userValidCredits = (user) => ((user.raw && user.raw.tags)
  ? user.raw.tags.some((e) => tagsCredits.includes(e))
  : false
);

module.exports = { tagsCredits, userValidCredits };
