// plus || plusclean || pluslite
const isAnyPlus = (type) => type && type.indexOf('plus') !== -1;

// clean || plusclean
const isAnyClean = (type) => type && type.indexOf('clean') !== -1;

// lite || pluslite
const isAnyLite = (type) => type && type.indexOf('lite') !== -1;

const isAnyFull = (type) => type === 'plus' || type === 'full';

const isPlus = (type) => type === 'plus';

const isPlusLite = (type) => type === 'pluslite';

const isPlusClean = (type) => type === 'plusclean';

const isFull = (type) => type === 'full';

const isClean = (type) => type === 'clean';

const isLite = (type) => type === 'lite';

const isNone = (type) => type === 'none';

const isHidden = (type) => type === 'hidden';

const isInternal = (type) => type === 'internal';

module.exports = {
  isAnyPlus,
  isAnyClean,
  isAnyLite,
  isPlus,
  isPlusClean,
  isPlusLite,
  isFull,
  isClean,
  isLite,
  isNone,
  isHidden,
  isInternal,
  isAnyFull,
};
