const none = require('./none');
const base = require('./base');
const webview = require('./webview');

const Types = Object.freeze({
  base: {
    name: 'base',
    invoke: base,
  },
  none: {
    name: 'none',
    invoke: none,
  },
  webView: {
    name: 'webView',
    invoke: webview,
  },
});

function get(propType) {
  return Types[propType] || Types.base;
}

module.exports = {
  Types,
  get,
};
