const granularStrategy = require('./granular');

const ALLOWED_STRATEGIES = {
  granular: granularStrategy,
};

function getStrategyByName(strategy) {
  if (!ALLOWED_STRATEGIES[strategy]) {
    throw new Error(`frontend-lazy options.splitChunks only supports "granular" chunk strategy, ${strategy} provided`);
  }
  return ALLOWED_STRATEGIES[strategy];
}

module.exports = {
  getStrategyByName,
};
