const CHECK_LEVEL = {
  WARN: 'WARN',
  ERROR: 'ERROR',
  OFF: 'OFF',
};

const DEFAULT_CHECK_LEVEL = CHECK_LEVEL.WARN;

function fromString(level) {
  if (!level || typeof level !== 'string') {
    return DEFAULT_CHECK_LEVEL;
  }

  return CHECK_LEVEL[level.toUpperCase()] || DEFAULT_CHECK_LEVEL;
}

Object.defineProperty(CHECK_LEVEL, 'fromString', {
  enumerable: false,
  configurable: false,
  writable: false,
  value: fromString,
});

module.exports = CHECK_LEVEL;
