const getCookieGuest = require('../../../src/utils/get-cookie-guest');

describe('Utils > getCookie', () => {
  it('should return preview cookie param if its present', () => {
    const mockReq = { cookies: { _ml_gc: 'abcd' } };

    expect(getCookieGuest(mockReq)).toBe('abcd');
  });

  it('should return undefined if cookies is not present', () => {
    expect(getCookieGuest({})).toBe(undefined);
  });

  it('should return undefined if _ml_gc param is not present in cookie', () => {
    const mockReq = { cookies: {} };

    expect(getCookieGuest(mockReq)).toBe(undefined);
  });
});
