const getPreview = require('../../../src/utils/get-preview');

describe('Utils > getPreview', () => {
  it('should return preview query param if its present', () => {
    const mockReq = { query: { preview: 'true' } };

    expect(getPreview(mockReq)).toBe('true');
  });

  it('should return undefined if query is not present', () => {
    expect(getPreview({})).toBe(undefined);
  });

  it('should return undefined if preview query param is not present in query', () => {
    const mockReq = { query: {} };

    expect(getPreview(mockReq)).toBe(undefined);
  });
});
