/**
 * Module dependencies
 */
const httpMocks = require('node-mocks-http');
const getSection = require('../../../src/utils/get-section');

/**
 * Tests
 */
describe('Utils > getSection', () => {
  test('if the request doesn\'t have the info we look, it should return its default value "home"', () => {
    const req = httpMocks.createRequest();

    expect(getSection(req)).toBe('home');
  });

  test('if the request has the header we are looking for, it should return its value', () => {
    const req = httpMocks.createRequest({
      headers: {
        'X-MShops-Section': 'search',
      },
    });

    expect(getSection(req)).toBe('search');
  });
});
