const isFeatureEnabled = require('../../../src/utils/is-feature-enabled');

describe('Utils -> isFeatureEnabled', () => {
  it('should return true', () => {
    const mockData = {
      shop: {
        features: [
          'mshops-guest-cho',
        ],
      },
    };

    expect(isFeatureEnabled(mockData, 'mshops-guest-cho')).toBeTruthy();
  });

  it('should return false', () => {
    const mockData = {
      shop: {
        features: [],
      },
    };

    expect(isFeatureEnabled(mockData, 'mshops-guest-cho')).toBeFalsy();
  });
});
