/**
 * Module dependencies
 */
const platforms = require('../config/domains.json');
const platform = require('../src/platform');

const domains = platforms.map(p => p.domain);
const domainsLength = domains.length;

console.time('multi-platform');

for (let i = 1; i < 1E5; i += 1) {
  const host = domains[i % domainsLength];
  platform(`https://www.${host}`);
}

console.timeEnd('multi-platform');
