const logger = require('frontend-logger')('prevent-open-redirect');
const statsdInternal = require('frontend-statsd/internal');

function log(url, platform) {
  logger.info(`PreventOpenRedirect Middleware - Invalid url to redirect: ${url}`, { siteId: platform.siteId });
  statsdInternal.increment('nordic.prevent_open_redirect.error.count', { site_id: platform.siteId });
}


module.exports = log;
