const feConfig = require('frontend-config');

exports.getDefaultUrl = function getDefaultUrl({ id, siteId }) {
  const baseDomain = feConfig.get('url.baseDomain.link', id, siteId);
  const indexOfDomainStart = baseDomain.indexOf('//') + 2;
  const domain = baseDomain.substring(indexOfDomainStart);

  return `https://${domain}`;
};
