/**
 * Location params
 */
const { protocol, host, pathname } = window.location;
const needsNormalization = pathname[pathname.length - 1] === '/';
const normalizedPath = needsNormalization ? pathname.substring(0, pathname.length - 1) : pathname;

/**
 * Default configuration
 * @see https://github.com/mzabriskie/axios#request-config
 */
module.exports = {
  baseURL: `${protocol}//${host}${normalizedPath}/api`,
  method: 'get',
  timeout: 3000,
  responseType: 'json',
  maxRedirects: 0,
  sendCSRFToken: true,
};
