/**
 * Module dependencies
 */
const { v4: uuidV4 } = require('uuid');

/**
 * Create and return a restclient flow context.
 * The context will be pass as headers in each request.
 */
function buildFlowStarterContext() {
  return {
    'x-request-id': uuidV4(),
    'x-flow-starter': true,
  };
}

/**
 * Expose buildFlowStarterContext
 */
module.exports = buildFlowStarterContext;
