const { NOT_IMPLEMENTED_HAS_TO_RETRY } = require('../constants/errorMessages');

/**
* Implements the template class for all the retry policies
*/
class RetryPolicy {
  /**
  * Define if the given error is valid candidate for retry
  * @summary This method should be overwritten by it's users on your custom implementations
  * @param {error} Object - The error object
  * @returns {undefined} This is the base implementation mockup, it will always throw an error on direct usage
  */
  static hasToRetry() {
    throw new Error(NOT_IMPLEMENTED_HAS_TO_RETRY);
  }
}

module.exports = RetryPolicy;
