import type { Request } from 'express';
import type { ScriptComponent, ScriptProps } from './index';

interface BundlerConfig {
  basePath?: string;
  useCache?: boolean;
  manifest?: string;
  prefix?: string;
  enableJcorsLoading?: boolean;
}

declare class ScriptsBundler {
  constructor(config?: BundlerConfig)
  bundle(
    script: ScriptComponent,
    req: Request,
    prepend: string | string[] | ScriptProps | ScriptProps[],
    append: string | string[] | ScriptProps | ScriptProps[],
    chunkExtractor: unknown,
  ): Promise<{scripts: string, preload: string}>
}

export = ScriptsBundler;
