import type { Request, Response } from 'express';
import type { StyleComponent } from './index';

interface BundlerConfig {
  basePath?: string;
  criticalKey?: string;
  criticalCookiePath?: string;
  criticalCookieDomain?: string;
  useCache?: boolean;
  manifest?: string;
  useManifest?: boolean;
  prefix?: string;
}

declare class StylesBundler {
  constructor(config?: BundlerConfig)
  bundle(req: Request, res: Response, style: StyleComponent): Promise<{styles: string, preload: string}>
}

export = StylesBundler;
