import type { ComponentType } from 'react';

declare const Style: Style.StyleComponent;

export = Style;

declare namespace Style {
  export type StyleProps = {
    href?: string;
    media?: string;
    preload?: boolean;
    inline?: boolean;
    critical?: boolean;
    children?: string | JSX.Element;
    renderChildrenInPlace?: boolean;
    nonceCSP?: string;
    firstRequestInlineStyles?: {
      key: string;
      value: string;
    };
  };

  export type StyleComponent = ComponentType<Style.StyleProps> & {
    peek: () => Style.StyleProps[];
    rewind: () => Style.StyleProps[];
  };
}

