(function ready(doc) {
  if (window.localStorage) {
    const stateDropdown = doc.getElementById('user-menu__coins-state');
    const storageStateDropdown = localStorage.getItem('opensd');
    if (storageStateDropdown === 'true' || storageStateDropdown == null) {
      stateDropdown.checked = true;
    } else {
      stateDropdown.checked = false;
    }
    document.addEventListener('DOMContentLoaded', () => {
      const main = document.getElementById('user-menu__coins-main');

      if (main) {
        main.addEventListener('click', () => {
          localStorage.setItem('opensd', !stateDropdown.checked);
        });
      }
    });
  }
}(document));
