const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const LinkTag = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Link Tag Component', () => {
    it('matches the default snapshot for link tag', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <LinkTag name="Nuevo" />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the snapshot for small link tag', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <LinkTag
          name="Nuevo"
          small
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
