const { getValue } = require('ml-navigation-config');

const linkTagProps = ({ req, platform }) => {
  const linkTagsConfig = getValue({
    key: 'linkTags',
    req,
    platform,
  }) || [];

  return {
    linkTags: linkTagsConfig
      .reduce((hash, linkTag) => {
        hash[linkTag.linkId] = linkTag;
        return hash;
      }, {}),
  };
};

module.exports = linkTagProps;
