const defaultProps = {
  req: {
    device: {
      mobile: true,
    },
  },
  platform: {
    id: 'ML',
    siteId: 'MLA',
  },
};

describe('Mercado Libre Navigation', () => {
  describe('Link Tag props', () => {
    it('matches the default snapshot for link tag', () => {
      const getValue = jest.fn().mockReturnValue([{
        linkId: 'some-id',
      }, {
        linkId: 'some-other-id',
      }]);
      jest.doMock('ml-navigation-config', () => ({ getValue }));

      const props = require('./props');

      const result = props(defaultProps);

      expect(result).toEqual({
        linkTags: {
          'some-id': {
            linkId: 'some-id',
          },
          'some-other-id': {
            linkId: 'some-other-id',
          },
        },
      });
    });
  });
});
