const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Copyright = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Copyright', () => {
    it('matches the default snapshot for Copyright for desktop', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Copyright
          since="1999"
          isMobile={false}
          companyName="Mercado Libre"
          currentYear={2019}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Copyright for mobile', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Copyright
          since="1999"
          isMobile
          companyName="Mercado Libre"
          currentYear={2019}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
