const { getValue } = require('ml-navigation-config');
const isMobile = require('../../../utils/is-mobile');

const copyrightProps = (props) => {
  const { req, platform } = props;
  const date = new Date();

  const since = getValue({
    key: 'copyrightSince',
    req,
    platform,
  });

  const companyName = getValue({
    key: 'companyName',
    req,
    platform,
  });

  return {
    isMobile: isMobile(props),
    companyName,
    since,
    currentYear: date.getFullYear(),
  };
};

module.exports = copyrightProps;
