/* eslint-disable react/no-danger */
const React = require('react');
const PropTypes = require('prop-types');

const namespace = 'nav-footer-downloadapp';
const DownloadAppBanner = (props) => {
  const { link, label, asButton, target } = props;
  const className = asButton ? `${namespace}-wrapper` : `${namespace}-banner`;

  return (
    <div className={className}>
      <a className={`${namespace}`} target={target} id="footer-applink" href={link}>
        <i className="nav-icon nav-icon-downloadapp" />
        <span dangerouslySetInnerHTML={{ __html: label }} />
      </a>
    </div>
  );
};

DownloadAppBanner.propTypes = {
  link: PropTypes.string.isRequired,
  label: PropTypes.string.isRequired,
  target: PropTypes.string,
  asButton: PropTypes.bool.isRequired,
};

DownloadAppBanner.defaultProps = {
  asButton: false,
  target: undefined,
};

module.exports = DownloadAppBanner;
