const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const DownloadAppBanner = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Download App Banner', () => {
    it('matches the default snapshot for download app banner', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <DownloadAppBanner
          link="https://www.mercadolibre.com.ar/descargar-app"
          label="Descargar App"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for download app banner as button', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <DownloadAppBanner
          link="https://www.mercadolibre.com.ar/descargar-app"
          label="Descargar App"
          asButton
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
