const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const GroupedMenu = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Grouped Menu', () => {
    it('matches the default snapshot for Grouped Menu', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <GroupedMenu
          id="about"
          title="Acerca de"
          links={[{
            id: 'news',
            name: 'Noticias',
            link: { href: 'https://ideas.mercadolibre.com/ar/noticias/' },
          }]}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Grouped Menu with an image', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <GroupedMenu
          id="about"
          title="Acerca de"
          links={[
            {
              id: 'news',
              name: 'Noticias',
              link: { href: 'https://ideas.mercadolibre.com/ar/noticias/' },
            },
            {
              id: 'image',
              name: 'Imagen',
              link: {
                href: 'https://www.mercadolibre.com.ar',
              },
              icon: {
                image: 'https://image.jpeg',
                width: '123',
                height: '124',
              },
            },
          ]}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
