const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const FooterAccess = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Footer', () => {
    it('matches the default snapshot for Footer Access', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterAccess
          title="Más información"
          groups={[
            {
              id: 'about',
              title: 'Acerca de',
              links: [{ link: { href: 'https://ideas.mercadolibre.com/ar/noticias/' } }],
            }]}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for empty Footer Access', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterAccess
          groups={[]}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
