const React = require('react');
const SIC = require('../sites/sic');

const tertiaries = [
  SIC,
];

const footerTertiaries = (props) => {
  const applicableTertiaries = [];

  tertiaries.forEach((tertiary) => {
    if (tertiary.doesApply(props)) {
      const { Tertiary, props: tertiaryProps } = tertiary;
      applicableTertiaries.push(<Tertiary {...tertiaryProps(props)} />);
    }
  });

  return applicableTertiaries.length > 0 ? applicableTertiaries : null;
};

module.exports = footerTertiaries;
