const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const HoneypathTrap = require('.');

describe('Mercado Libre Navigation', () => {
  describe('HoneypathTrap', () => {
    it('matches the snapshot for HoneypathTrap when required props are not present', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <HoneypathTrap />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the snapshot for HoneypathTrap when all props are ok', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <HoneypathTrap
          url="https://hp.mercadolibre.com/"
          label="Mercado Libre"
          platformId="ML"
          siteId="MLA"
          deviceType="mobile"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the snapshot for HoneypathTrap when renderd in a webview', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <HoneypathTrap
          url="https://hp.mercadolibre.com/"
          label="Mercado Libre"
          webView
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
