/* eslint-disable security/detect-non-literal-fs-filename */
const sic = require('./sic');

const additionalLinks = [
  sic,
];

const addLinks = (links, props) => {
  additionalLinks.forEach((additionalLink) => {
    if (additionalLink.doesApply(props)) {
      const additionalLinkProps = additionalLink.props(props);
      links.push(additionalLinkProps.link);
    }
  });
};

module.exports = {
  addLinks,
};
