const React = require('react');
const PropTypes = require('prop-types');
const Image = require('frontend-image');

const SICLogo = (props) => {
  const { url, image, bigImage, alt, width, height, imageClassName } = props;

  return (
    <a className="nav-footer-sic-logo" href={url} target="_blank" rel="nofollow noreferrer">
      <Image
        className={imageClassName}
        src={image}
        width={width}
        height={height}
        srcSet={bigImage}
        alt={alt}
        onload="off"
      />
    </a>
  );
};

SICLogo.propTypes = {
  url: PropTypes.string.isRequired,
  image: PropTypes.string.isRequired,
  bigImage: PropTypes.string.isRequired,
  alt: PropTypes.string.isRequired,
  width: PropTypes.string,
  imageClassName: PropTypes.string,
  height: PropTypes.string,
};

SICLogo.defaultProps = {
  width: '141',
  height: '30',
  imageClassName: undefined,
};

module.exports = SICLogo;

