const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const SICLogo = require('./logo');

describe('Mercado Libre Navigation', () => {
  describe('SIC Logo', () => {
    it('matches the default snapshot for SIC logo', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <SICLogo
          url="https://www.mercadolibre.com.ar"
          image="https://www.mercadolibre.com.ar/image.jpg"
          bigImage="https://www.mercadolibre.com.ar/image.jpg"
          alt="SIC"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});

