const React = require('react');
const PropTypes = require('prop-types');
const Logo = require('./logo');

const SIC = (props) => {
  const { sic, pum, link, isMobile } = props;
  return (
    <>
      <Logo {...sic} />
      <Logo {...pum} />
      {isMobile && <a className="nav-footer-sic-link" href={link.href} target="_blank" rel="nofollow noreferrer">{link.name}</a> }
    </>
  );
};

SIC.propTypes = {
  sic: PropTypes.object.isRequired,
  pum: PropTypes.object.isRequired,
  link: PropTypes.shape({
    href: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
  }),
  isMobile: PropTypes.bool.isRequired,
};

SIC.defaultProps = {
  link: null,
};

module.exports = SIC;

