const { getUserPictures } = require('@frontend-navigations/commons/user-pictures');

const loggedInFooterUserMenuProps = (props) => {
  const { user, navigationPIIData, navigationUrls, device } = props;

  const username = navigationPIIData.preferredName || user.nickname;

  return {
    lazyLoad: device.desktop ? 'off' : 'on',
    link: navigationUrls.myAccount.link,
    username,
    ...getUserPictures(props),
  };
};

module.exports = loggedInFooterUserMenuProps;
