const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Footer = require('.');

describe('Mercado Libre Navigation', () => {
  describe('Footer', () => {
    it('matches the default snapshot for Footer in desktop', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Footer
          footerAccess={{ show: true }}
          isDesktop
          navigation={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Footer in desktop in lite mode', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Footer
          type="lite"
          footerAccess={{ show: true }}
          isDesktop
          navigation={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Footer in desktop in clean mode', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Footer
          type="plusclean"
          footerAccess={{ show: true }}
          isDesktop
          navigation={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Footer in mobile', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Footer
          footerAccess={{ show: true }}
          isDesktop
          userMenu={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/registration',
              name: 'Cre치 tu cuenta',
              id: 'registration',
            }],
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Footer in mobile in lite mode', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Footer
          type="lite"
          footerAccess={{ show: true }}
          isDesktop={false}
          userMenu={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/registration',
              name: 'Cre치 tu cuenta',
              id: 'registration',
            }],
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Footer in mobile in clean mode, which renders the lite version', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Footer
          type="clean"
          footerAccess={{ show: true }}
          isDesktop={false}
          userMenu={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/registration',
              name: 'Cre치 tu cuenta',
              id: 'registration',
            }],
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('renders nothing since it is hidden mode', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Footer
          type="hidden"
          footerAccess={{ show: true }}
          isDesktop={false}
          userMenu={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/registration',
              name: 'Cre치 tu cuenta',
              id: 'registration',
            }],
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
