const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const FooterDesktop = require('./desktop');

describe('Mercado Libre Navigation', () => {
  describe('Footer Lite Desktop', () => {
    it('matches the default snapshot for Footer Lite Desktop in desktop', () => {
      const downloadApp = {
        'header-top': [{
          type: 'html',
          typeData: {
            content: '<div>headerTop</div>',
          },
        }],
        'footer-top': [{
          type: 'html',
          typeData: {
            content: '<div>footerTop</div>',
          },
        }],
        'footer-bottom': [{
          type: 'html',
          typeData: {
            content: '<div>footerBottom</div>',
          },
        }],
      };
      const renderer = new ShallowRenderer();
      const component = (
        <FooterDesktop
          footerNavigation={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/jobs',
              name: 'Trabaja con nosotros',
              id: 'jobs',
            }],
          }}
          copyright={{
            since: '1999',
            companyName: 'Mercado Libre',
            currentYear: 2019,
          }}
          downloadAppBanner={{
            link: 'https://www.mercadolibre.com.ar',
            label: 'Comprá y vendé en la app',
          }}
          downloadApp={downloadApp}
          userMenu={{
            links: [{
              href: 'https://www.mercadolibre.com.ar/registration',
              name: 'Creá tu cuenta',
              id: 'registration',
            }],
            isUserLoggedIn: false,
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
